mapper.select:
select 

from studentbehavior
where id = #{id,jdbcType=INTEGER}

select count(*) from (
select distinct studentId,breakfastLabel from scoreanalysis.studentbehavior) ass
where breakfastLabel = #{breakfastLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss ,scoreanalysis.student as s
where s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.breakfastLabel = #{breakfastLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss , scoreanalysis.class as c ,scoreanalysis.student as s
where s.classId=c.classId and s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.breakfastLabel = #{breakfastLabel,jdbcType=INTEGER}
and (c.collegeId=#{id,jdbcType=VARCHAR} or c.majorId=#{id,jdbcType=VARCHAR});

select count(*) from (
select distinct studentId,consumptionLabel from scoreanalysis.studentbehavior) ass
where consumptionLabel = #{consumptionLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss ,scoreanalysis.student as s
where s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.consumptionLabel = #{consumptionLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss , scoreanalysis.class as c ,scoreanalysis.student as s
where s.classId=c.classId and s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.consumptionLabel = #{consumptionLabel,jdbcType=INTEGER}
and (c.collegeId=#{id,jdbcType=VARCHAR} or c.majorId=#{id,jdbcType=VARCHAR});

select count(*) from (
select distinct studentId,mealLabel from scoreanalysis.studentbehavior) ass
where mealLabel = #{mealLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss ,scoreanalysis.student as s
where s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.mealLabel = #{mealLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss , scoreanalysis.class as c ,scoreanalysis.student as s
where s.classId=c.classId and s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.mealLabel = #{mealLabel,jdbcType=INTEGER}
and (c.collegeId=#{id,jdbcType=VARCHAR} or c.majorId=#{id,jdbcType=VARCHAR});

select count(*) from (
select distinct studentId,sleeplateLabel from scoreanalysis.studentbehavior) ass
where sleeplateLabel = #{sleeplateLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss ,scoreanalysis.student as s
where s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.sleeplateLabel = #{sleeplateLabel,jdbcType=INTEGER};

select count(*)
from scoreanalysis.studentbehavior as ss , scoreanalysis.class as c ,scoreanalysis.student as s
where s.classId=c.classId and s.studentId=ss.studentId
and ss.year like #{year,jdbcType=VARCHAR}
and ss.sleeplateLabel = #{sleeplateLabel,jdbcType=INTEGER}
and (c.collegeId=#{id,jdbcType=VARCHAR} or c.majorId=#{id,jdbcType=VARCHAR});
